"use client";
import { useRef, useEffect } from "react";
import { motion } from "framer-motion";
import { cn } from "@/lib/utils";

interface LampHeadingProps {
  text: string;
  className?: string;
  gradientColors?: {
    from: string;
    to: string;
  };
  lineHeight?: number;
  glowIntensity?: number;
  glowSize?: number;
  animationSpeed?: number;
  direction?: "above" | "below";
}

export const LampHeading = ({
  text,
  className,
  gradientColors = { from: "#ff3366", to: "#338ef7" },
  lineHeight = 2,
  glowIntensity = 0.7,
  glowSize = 20,
  animationSpeed = 3,
  direction = "below",
}: LampHeadingProps) => {
  const mainLineRef = useRef<HTMLDivElement>(null);
  const flowAnimation = {
    animate: {
      backgroundPosition: ["0% 50%", "100% 50%", "0% 50%"],
      transition: {
        duration: animationSpeed,
        ease: "easeInOut",
        repeat: Infinity,
      },
    },
  };

  useEffect(() => {
    if (mainLineRef.current) {
      mainLineRef.current.style.backgroundSize = "200% 200%";
    }
  }, []);
  const getGlowStyles = (size: number, intensity: number, index: number) => {
    const baseStyles = {
      position: "absolute" as const,
      width: "100%",
      height: `${size}px`,
      background: `linear-gradient(to right, ${gradientColors.from}, ${gradientColors.to}, ${gradientColors.from})`,
      filter: `blur(${glowSize / (index + 1)}px)`,
      opacity: intensity,
      left: 0,
      backgroundSize: "200% 200%",
    };
    if (direction === "below") {
      return {
        ...baseStyles,
        top: `${lineHeight + 1}px`,
        transformOrigin: "center top",
      };
    } else {
      return {
        ...baseStyles,
        bottom: `${lineHeight + 1}px`,
        transformOrigin: "center bottom",
      };
    }
  };

  return (
    <div className={cn("flex flex-col items-start", className)}>
      <h2 className="font-medium tracking-wide">{text}</h2>
      <div className="w-full relative mt-1">
        <motion.div
          variants={flowAnimation}
          animate="animate"
          style={getGlowStyles(
            Math.max(8, lineHeight * 3),
            glowIntensity * 0.7,
            1,
          )}
          className="rounded-full"
        />

        <motion.div
          variants={flowAnimation}
          animate="animate"
          style={getGlowStyles(Math.max(4, lineHeight * 2), glowIntensity, 2)}
          className="rounded-full"
          transition={{
            duration: animationSpeed * 0.8,
          }}
        />

        <motion.div
          ref={mainLineRef}
          variants={flowAnimation}
          animate="animate"
          style={{
            height: `${lineHeight}px`,
            background: `linear-gradient(to right, ${gradientColors.from}, ${gradientColors.to}, ${gradientColors.from})`,
            position: "relative",
            zIndex: 10,
            backgroundSize: "200% 200%",
          }}
          className="w-full rounded-full"
        />
      </div>
    </div>
  );
};

export default LampHeading;

import clsx, { ClassValue } from "clsx";
import { Metadata } from "next";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

export function humanize(name: string): string {
  return name
    .replace(/-/g, " ")
    .replace(/([A-Z])/g, " $1")
    .trim()
    .split(/\s+/)
    .map((word) => word[0].toUpperCase() + word.substring(1).toLowerCase())
    .join(" ");
}

export const truncate = (str: string | null, length: number) => {
  if (!str || str.length <= length) return str;
  return `${str.slice(0, length - 3)}...`;
};

export const fetcher = (...args: Parameters<typeof fetch>) =>
  fetch(...args).then((res) => res.json());

export const capitalize = (str: string, lower = false) =>
  (lower ? str.toLowerCase() : str).replace(/(?:^|\s|["'([{])+\S/g, (match) =>
    match.toUpperCase(),
  );

export function formatDate(input: string | number): string {
  const date = new Date(input);
  return date.toLocaleDateString("en-US", {
    month: "long",
    day: "numeric",
    year: "numeric",
  });
}

export function absoluteUrl(path: string) {
  return `${process.env.NEXT_PUBLIC_APP_URL || ""}${path}`;
}

export function constructMetadata({
  title = "Nuvyx UI - Modern React + Tailwind CSS + Motion components & Templates",
  description = "Nuvyx UI is a curated collection of the best landing page components built using React + Tailwind CSS + Motion",
  image = absoluteUrl("/og"),
  ...props
}: {
  title?: string;
  description?: string;
  image?: string;
  [key: string]: Metadata[keyof Metadata];
}): Metadata {
  return {
    title,
    description,
    keywords: [
      "React",
      "Tailwind CSS",
      "Motion",
      "Landing Page",
      "Components",
      "Next.js",
    ],
    openGraph: {
      title,
      description,
      type: "website",
      images: [
        {
          url: image,
          width: 1200,
          height: 630,
        },
      ],
    },
    twitter: {
      card: "summary_large_image",
      title,
      description,
      images: [image],
      creator: "@mihirjaiswal",
    },
    icons: "/favicon.ico",
    metadataBase: new URL("https://nuvyxui.vercel.app/"),
    authors: [
      {
        name: "Mihir Jaiswal",
        url: "https://github.com/MihirJaiswal",
      },
    ],
    creator: "mihirjaiswal",
    ...props,
  };
}
