"use client";
import type React from "react";
import { useRef, useEffect, useState, useMemo } from "react";
import { cn } from "@/lib/utils";

interface Drop {
  x: number;
  y: number;
  radius: number;
  maxRadius: number;
  speed: number;
  opacity: number;
  color: string;
}

export interface DynamicRippleProps
  extends React.HTMLAttributes<HTMLDivElement> {
  theme?: "blue" | "purple" | "green" | "amber" | "rose" | "custom";
  customColors?: {
    primary: string;
    secondary: string;
  };
  intensity?: 1 | 2 | 3 | 4 | 5;
  speed?: 1 | 2 | 3 | 4 | 5;
  reactToCursor?: boolean;
  autoAnimate?: boolean;
  rounded?: "none" | "sm" | "md" | "lg" | "xl" | "full";
  gradientOverlay?: boolean;
  children: React.ReactNode;
}

export function DynamicRipple({
  theme = "blue",
  customColors,
  intensity = 3,
  speed = 3,
  reactToCursor = true,
  autoAnimate = true,
  rounded = "lg",
  gradientOverlay = true,
  className,
  children,
  ...props
}: DynamicRippleProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 });
  const animationRef = useRef<number>(0);
  const dropsRef = useRef<Drop[]>([]);

  const intensityFactors = useMemo(
    () => ({
      1: { size: 0.6, opacity: 0.3, count: 3 },
      2: { size: 0.8, opacity: 0.4, count: 5 },
      3: { size: 1.0, opacity: 0.5, count: 7 },
      4: { size: 1.2, opacity: 0.6, count: 9 },
      5: { size: 1.5, opacity: 0.7, count: 12 },
    }),
    [],
  );

  const speedFactors = useMemo(
    () => ({
      1: 0.5,
      2: 0.75,
      3: 1.0,
      4: 1.25,
      5: 1.5,
    }),
    [],
  );

  const themeColors = useMemo(
    () => ({
      blue: {
        primary: "rgba(59, 130, 246, 0.7)",
        secondary: "rgba(6, 182, 212, 0.7)",
        overlay: "from-blue-500/10 to-cyan-500/10",
      },
      purple: {
        primary: "rgba(139, 92, 246, 0.7)",
        secondary: "rgba(216, 180, 254, 0.7)",
        overlay: "from-purple-500/10 to-pink-500/10",
      },
      green: {
        primary: "rgba(16, 185, 129, 0.7)",
        secondary: "rgba(110, 231, 183, 0.7)",
        overlay: "from-green-500/10 to-emerald-500/10",
      },
      amber: {
        primary: "rgba(245, 158, 11, 0.7)",
        secondary: "rgba(252, 211, 77, 0.7)",
        overlay: "from-amber-500/10 to-yellow-500/10",
      },
      rose: {
        primary: "rgba(244, 63, 94, 0.7)",
        secondary: "rgba(251, 113, 133, 0.7)",
        overlay: "from-rose-500/10 to-pink-500/10",
      },
      custom: {
        primary: customColors?.primary || "rgba(59, 130, 246, 0.7)",
        secondary: customColors?.secondary || "rgba(6, 182, 212, 0.7)",
        overlay: "from-gray-500/10 to-gray-400/10",
      },
    }),
    [customColors],
  );

  const currentTheme = themeColors[theme];

  const roundedStyles = useMemo(
    () => ({
      none: "rounded-none",
      sm: "rounded-sm",
      md: "rounded-md",
      lg: "rounded-lg",
      xl: "rounded-xl",
      full: "rounded-full",
    }),
    [],
  );

  useEffect(() => {
    if (!canvasRef.current || !containerRef.current) return;

    const updateDimensions = () => {
      if (!containerRef.current) return;

      const { width, height } = containerRef.current.getBoundingClientRect();
      setDimensions({ width, height });

      if (canvasRef.current) {
        canvasRef.current.width = width;
        canvasRef.current.height = height;
      }
    };

    updateDimensions();

    const container = containerRef.current;
    const resizeObserver = new ResizeObserver(updateDimensions);
    resizeObserver.observe(container);

    return () => {
      resizeObserver.disconnect();
      cancelAnimationFrame(animationRef.current);
    };
  }, []);

  useEffect(() => {
    if (!canvasRef.current || !dimensions.width || !dimensions.height) return;

    const currentIntensityFactors = intensityFactors[intensity];
    const currentSpeedFactor = speedFactors[speed];

    const createDrop = (x: number, y: number, userInitiated = false) => {
      const maxRadius =
        Math.min(dimensions.width, dimensions.height) *
        0.3 *
        currentIntensityFactors.size;

      return {
        x,
        y,
        radius: 0,
        maxRadius,
        speed: currentSpeedFactor * (userInitiated ? 1.5 : 1),
        opacity: currentIntensityFactors.opacity * (userInitiated ? 1.2 : 1),
        color:
          Math.random() > 0.5 ? currentTheme.primary : currentTheme.secondary,
      };
    };

    if (autoAnimate) {
      const initialDrops = Array.from({
        length: currentIntensityFactors.count,
      }).map(() => {
        const x = Math.random() * dimensions.width;
        const y = Math.random() * dimensions.height;
        return createDrop(x, y);
      });

      dropsRef.current = initialDrops;
    }

    const handlePointerMove = (e: MouseEvent | TouchEvent) => {
      if (!reactToCursor || !containerRef.current) return;

      const rect = containerRef.current.getBoundingClientRect();
      let clientX, clientY;

      if ("touches" in e) {
        clientX = e.touches[0].clientX;
        clientY = e.touches[0].clientY;
      } else {
        clientX = e.clientX;
        clientY = e.clientY;
      }

      const x = clientX - rect.left;
      const y = clientY - rect.top;

      dropsRef.current.push(createDrop(x, y, true));

      if (dropsRef.current.length > 20) {
        dropsRef.current = dropsRef.current.slice(-20);
      }
    };

    const container = containerRef.current;
    if (reactToCursor && container) {
      container.addEventListener("mousemove", handlePointerMove);
      container.addEventListener("touchmove", handlePointerMove);
    }

    return () => {
      if (container) {
        container.removeEventListener("mousemove", handlePointerMove);
        container.removeEventListener("touchmove", handlePointerMove);
      }
    };
  }, [
    dimensions,
    intensity,
    speed,
    reactToCursor,
    autoAnimate,
    currentTheme.primary,
    currentTheme.secondary,
    intensityFactors,
    speedFactors,
  ]);

  useEffect(() => {
    if (!canvasRef.current || !dimensions.width || !dimensions.height) return;

    const ctx = canvasRef.current.getContext("2d");
    if (!ctx) return;

    const currentIntensityFactors = intensityFactors[intensity];
    const currentSpeedFactor = speedFactors[speed];

    const animate = () => {
      ctx.clearRect(0, 0, dimensions.width, dimensions.height);
      dropsRef.current = dropsRef.current.filter((drop) => {
        drop.radius += drop.speed;
        ctx.beginPath();
        ctx.arc(drop.x, drop.y, drop.radius, 0, Math.PI * 2);
        ctx.strokeStyle = drop.color;
        ctx.lineWidth = 2;
        ctx.globalAlpha = Math.max(
          0,
          drop.opacity * (1 - drop.radius / drop.maxRadius),
        );
        ctx.stroke();
        return drop.radius < drop.maxRadius;
      });

      if (autoAnimate && Math.random() < 0.05 * currentSpeedFactor) {
        const x = Math.random() * dimensions.width;
        const y = Math.random() * dimensions.height;

        dropsRef.current.push({
          x,
          y,
          radius: 0,
          maxRadius:
            Math.min(dimensions.width, dimensions.height) *
            0.2 *
            currentIntensityFactors.size,
          speed: currentSpeedFactor,
          opacity: currentIntensityFactors.opacity,
          color:
            Math.random() > 0.5 ? currentTheme.primary : currentTheme.secondary,
        });
      }

      animationRef.current = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      cancelAnimationFrame(animationRef.current);
    };
  }, [
    dimensions,
    intensity,
    speed,
    autoAnimate,
    currentTheme.primary,
    currentTheme.secondary,
    intensityFactors,
    speedFactors,
  ]);

  return (
    <div
      ref={containerRef}
      className={cn(
        "relative overflow-hidden",
        roundedStyles[rounded],
        className,
      )}
      {...props}
    >
      <canvas
        ref={canvasRef}
        className="absolute inset-0 w-full h-full pointer-events-none"
      />
      {gradientOverlay && (
        <div
          className={cn(
            "absolute inset-0 bg-gradient-to-br opacity-30 pointer-events-none",
            currentTheme.overlay,
          )}
        />
      )}
      <div className="relative z-10">{children}</div>
    </div>
  );
}

import clsx, { ClassValue } from "clsx";
import { Metadata } from "next";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}

export function humanize(name: string): string {
  return name
    .replace(/-/g, " ")
    .replace(/([A-Z])/g, " $1")
    .trim()
    .split(/\s+/)
    .map((word) => word[0].toUpperCase() + word.substring(1).toLowerCase())
    .join(" ");
}

export const truncate = (str: string | null, length: number) => {
  if (!str || str.length <= length) return str;
  return `${str.slice(0, length - 3)}...`;
};

export const fetcher = (...args: Parameters<typeof fetch>) =>
  fetch(...args).then((res) => res.json());

export const capitalize = (str: string, lower = false) =>
  (lower ? str.toLowerCase() : str).replace(/(?:^|\s|["'([{])+\S/g, (match) =>
    match.toUpperCase(),
  );

export function formatDate(input: string | number): string {
  const date = new Date(input);
  return date.toLocaleDateString("en-US", {
    month: "long",
    day: "numeric",
    year: "numeric",
  });
}

export function absoluteUrl(path: string) {
  return `${process.env.NEXT_PUBLIC_APP_URL || "https://nuvyxui.vercel.app"}${path}`;
}

export function constructMetadata({
  title = "Nuvyx UI - Modern React + Tailwind CSS + Motion components & Templates",
  description = "Nuvyx UI is a curated collection of the best landing page components built using React + Tailwind CSS + Motion",
  image = absoluteUrl("/og"),
  ...props
}: {
  title?: string;
  description?: string;
  image?: string;
  [key: string]: Metadata[keyof Metadata];
}): Metadata {
  return {
    title,
    description,
    keywords: [
      "React",
      "Tailwind CSS",
      "Motion",
      "Landing Page",
      "Components",
      "Next.js",
    ],
    openGraph: {
      title,
      description,
      type: "website",
      images: [
        {
          url: image,
          width: 1200,
          height: 630,
        },
      ],
    },
    twitter: {
      card: "summary_large_image",
      title,
      description,
      images: [image],
      creator: "@mihirjaiswal",
    },
    icons: "/favicon.ico",
    metadataBase: new URL("https://nuvyxui.vercel.app/"),
    authors: [
      {
        name: "Mihir Jaiswal",
        url: "https://github.com/MihirJaiswal",
      },
    ],
    creator: "mihirjaiswal",
    ...props,
  };
}

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const buttonVariants = cva(
  "inline-flex items-center justify-center rounded-xl text-sm font-medium transition-colors focus-visible:outline-none focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default:
          "bg-primary text-primary-foreground shadow hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground shadow-sm hover:bg-destructive/90",
        outline:
          "border border-input bg-background text-foreground shadow-sm hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground shadow-sm hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground",
        link: "text-primary underline-offset-4 hover:underline",
        rainbow:
          "group relative animate-rainbow cursor-pointer border-0 bg-[linear-gradient(#121213,#121213),linear-gradient(#121213_50%,rgba(18,18,19,0.6)_80%,rgba(18,18,19,0)),linear-gradient(90deg,hsl(var(--color-1)),hsl(var(--color-5)),hsl(var(--color-3)),hsl(var(--color-4)),hsl(var(--color-2)))] bg-[length:200%] text-primary-foreground [background-clip:padding-box,border-box,border-box] [background-origin:border-box] [border:calc(0.08*1rem)_solid_transparent] before:absolute before:bottom-[-20%] before:left-1/2 before:z-0 before:h-1/5 before:w-3/5 before:-translate-x-1/2 before:animate-rainbow before:bg-[linear-gradient(90deg,hsl(var(--color-1)),hsl(var(--color-5)),hsl(var(--color-3)),hsl(var(--color-4)),hsl(var(--color-2)))] before:[filter:blur(calc(0.8*1rem))] dark:bg-[linear-gradient(#fff,#fff),linear-gradient(#fff_50%,rgba(255,255,255,0.6)_80%,rgba(0,0,0,0)),linear-gradient(90deg,hsl(var(--color-1)),hsl(var(--color-5)),hsl(var(--color-3)),hsl(var(--color-4)),hsl(var(--color-2)))]",
        "rainbow-outline":
          "group relative animate-rainbow cursor-pointer border-0 border-input bg-[linear-gradient(#fff,#fff),linear-gradient(#fff_50%,rgba(255,255,255,0.6)_80%,rgba(0,0,0,0)),linear-gradient(90deg,hsl(var(--color-1)),hsl(var(--color-5)),hsl(var(--color-3)),hsl(var(--color-4)),hsl(var(--color-2)))] bg-[length:200%] px-4 text-foreground shadow-sm [background-clip:padding-box,border-box,border-box] [background-origin:border-box] [border:calc(0.08*1rem)_solid_transparent] before:absolute before:bottom-[-20%] before:left-1/2 before:z-0 before:h-1/5 before:w-3/5 before:-translate-x-1/2 before:animate-rainbow before:bg-[linear-gradient(90deg,hsl(var(--color-1)),hsl(var(--color-5)),hsl(var(--color-3)),hsl(var(--color-4)),hsl(var(--color-2)))] before:[filter:blur(calc(0.8*1rem))] dark:bg-[linear-gradient(#121213,#121213),linear-gradient(#121213_50%,rgba(18,18,19,0.6)_80%,rgba(18,18,19,0)),linear-gradient(90deg,hsl(var(--color-1)),hsl(var(--color-5)),hsl(var(--color-3)),hsl(var(--color-4)),hsl(var(--color-2)))]",
      },
      size: {
        default: "h-9 px-4 py-2",
        sm: "h-8 rounded-xl px-3 text-xs",
        lg: "h-11 rounded-xl px-8",
        icon: "size-9",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  },
);

interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button";
    return (
      <Comp
        data-slot="button"
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    );
  },
);

Button.displayName = "Button";

export { Button, buttonVariants, type ButtonProps };

import * as React from "react";
import { Slot } from "@radix-ui/react-slot";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const badgeVariants = cva(
  "inline-flex items-center justify-center rounded-md border px-2 py-0.5 text-xs font-medium w-fit whitespace-nowrap shrink-0 [&>svg]:size-3 gap-1 [&>svg]:pointer-events-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive transition-[color,box-shadow] overflow-hidden",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground [a&]:hover:bg-primary/90",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground [a&]:hover:bg-secondary/90",
        destructive:
          "border-transparent bg-destructive text-white [a&]:hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "text-foreground [a&]:hover:bg-accent [a&]:hover:text-accent-foreground",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

function Badge({
  className,
  variant,
  asChild = false,
  ...props
}: React.ComponentProps<"span"> &
  VariantProps<typeof badgeVariants> & { asChild?: boolean }) {
  const Comp = asChild ? Slot : "span";

  return (
    <Comp
      data-slot="badge"
      className={cn(badgeVariants({ variant }), className)}
      {...props}
    />
  );
}

export { Badge, badgeVariants };
